import axios from "axios";

export const TestModule = {
    state: () => ({
        users: [
            {id: 0, avatar:'https://img2.akspic.ru/previews/3/9/1/8/6/168193/168193-paren_v_shleme-motocikletnyj_shlem-motocikl-shlem-odezhda-500x.jpg', name: 'Гоша', email: 'user@mail.net', city: 'Казань', phone: '+7(917)-353-33-55', site: 'https://site.com'},
            {id: 1, avatar:'https://klike.net/uploads/posts/2019-03/1551511801_1.jpg', name: 'Маша', email: 'user@mail.net', city: 'Рязань', phone: '+7(947)-855-36-11', site: 'https://site.com'},
            {id: 2, avatar:'https://i.pinimg.com/736x/14/df/bf/14dfbf0e75988b5daa3aaccddba0eb6d.jpg', name: 'Даша', email: 'user@mail.net', city: 'Рязань', phone: '+7(947)-954-23-62', site: 'https://site.com'},
            {id: 3, avatar:'https://i.yapx.ru/Ra8I0.jpg', name: 'Юля', email: 'user@mail.net', city: 'Рязань', phone: '+7(947)-652-03-21', site: 'https://site.com'},
            {id: 4, avatar:'https://android-obzor.com/wp-content/uploads/2022/02/1-2-300x300.jpg', name: 'Зуля', email: 'user@mail.net', city: 'Белгород', phone: '+7(947)-550-70-39', site: 'https://site.com'},
            {id: 5, avatar:'https://p16-sign-sg.tiktokcdn.com/aweme/720x720/tos-alisg-avt-0068/8d2c78cdc7ee3cffd5e3b3bc22a87c10.jpeg?x-expires=1658012400&x-signature=SEooVfaPyVQMG3DDNP7QXOeGSGg%3D', name: 'Миша', email: 'user@mail.net', city: 'Москва', phone: '+7(947)-257-63-72', site: 'https://site.com'},
            {id: 6, avatar:'https://i.pinimg.com/736x/86/95/54/8695540db1e9224367ed9d1a4884ccfc.jpg', name: 'Гриша', email: 'user@mail.net', city: 'Москва', phone: '+7(947)-153-83-11', site: 'https://site.com'},
            {id: 7, avatar:'https://www.meme-arsenal.com/memes/6bd0fe45e3ddadd122fd2aa9b626e56b.jpg', name: 'Ксюша', email: 'user@mail.net', city: 'Казань', phone: '+7(947)-651-31-13', site: 'https://site.com'},
        ],
        page: 1,
        limit: 5,
        totalPages: ''
    }),
    getters: {

    },
    mutations: {
        setUsers(state, users) {
            state.users = users;
        },
        setPage(state, page) {
            state.page = page;
        },
        setTotalPages(state, totalPages) {
            state.users = totalPages;
        },
        addUser (state, data) {
            state.users.push(data);
        },
        removeUser (state, user){
            state.users = state.users.filter(p => p.id !== user.id);
        }
    },
    actions: {
        async fetchUsers({state, commit}) {
            try {
                const response = await axios.get('/api/users', {
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    params: {
                        _page: state.page,
                        _limit: state.limit
                    }
                });
                commit('setTotalPages', Math.ceil(response.headers['x-total-count'] / state.limit))
                commit('setUsers', response.data)
            } catch (e) {
                console.log(e)
            }
        },
    },
    namespaced: true
}
